$enter_password = Read-Host 'Enter You Password' -AsSecureString

$password = [Runtime.InteropServices.Marshal]::PtrToStringAuto([Runtime.InteropServices.Marshal]::SecureStringToBSTR($enter_password))

$password = $password.Replace('%','{%}').Replace('^','{^}')

if(!$password){return}

$ImageBackground = @'

'@

function DecodeBase64Image {
    param ([Parameter(Mandatory=$true)][String]$ImageBase64)
    $ObjBitmapImage = New-Object System.Windows.Media.Imaging.BitmapImage
    $ObjBitmapImage.BeginInit()
    $ObjBitmapImage.StreamSource = [System.IO.MemoryStream][System.Convert]::FromBase64String($ImageBase64)
    $ObjBitmapImage.EndInit()
    $ObjBitmapImage.Freeze()
    $ObjBitmapImage
}

Add-Type -AssemblyName PresentationFramework, PresentationCore, WindowsBase, System.Windows.Forms, System.Drawing
[xml]$xaml = @"
<Window xmlns="http://schemas.microsoft.com/winfx/2006/xaml/presentation"
        xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml"
        xmlns:d="http://schemas.microsoft.com/expression/blend/2008"
        xmlns:mc="http://schemas.openxmlformats.org/markup-compatibility/2006"
        Title="MainWindow" Height="64" Width="64" ResizeMode="NoResize"
        Topmost="True" WindowStartupLocation="CenterScreen"
        WindowStyle="None" AllowsTransparency="true" Background="Transparent">
    <Grid>
        <Image Name="BackgroundImage" HorizontalAlignment="Left" Height="64" VerticalAlignment="Top" Width="64"/>
        <CheckBox Name="Clalit" IsChecked="True" HorizontalAlignment="Left" Margin="2,25,0,0" VerticalAlignment="Top"/>
        <Button Name="Send" Content="התחבר" HorizontalAlignment="Left" Margin="0,40,0,0" VerticalAlignment="Top" Width="64"/>
        <Button Name="Close" Content="X" HorizontalAlignment="Left" Margin="48,0,0,0" VerticalAlignment="Top" Width="16"/>
    </Grid>
</Window>
"@ 

$Reader = (New-Object System.Xml.XmlNodeReader $xaml)  
$Form = [Windows.Markup.XamlReader]::Load($reader) 

$xaml.SelectNodes("//*[@*[contains(translate(name(.),'n','N'),'Name')]]") | ForEach-Object { New-Variable -Name $_.Name -Value $Form.FindName($_.Name) -Force }

$BackgroundImage.Source = DecodeBase64Image -ImageBase64 $ImageBackground

$wshell = New-Object -ComObject wscript.shell;

$Send.Add_Click({

Sleep 3
if($Clalit.IsChecked){$wshell.SendKeys("$env:USERNAME@$env:USERDOMAIN")}else{$wshell.SendKeys("$env:USERNAME")}
$wshell.SendKeys("{tab}")
$wshell.SendKeys("$password")
$wshell.SendKeys("{enter}")

})

$Close.Add_Click({$Form.Close()})

$Form.add_MouseLeftButtonDown({$_.handled=$true;$this.DragMove()})

$Form.ShowInTaskbar = $false

[void]$Form.ShowDialog()
